import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'

const Home = () => {
    const [getdata, setGetdata] = useState([]);

    const getData = async (e) => {

        const { fname, email, pass, mno, address } = getdata;

        const res = await fetch("/getdata", {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            },
        });

        const data = await res.json();
        console.log(data);

        if (res.status === 404 || !data) {
            console.log("error");
        }
        else {
            setGetdata(data);
            console.log("Get data successfully...");
        }
    }

    useEffect(() => {
        getData();
    }, []);


    return (
        <>
            <div className="mt-5">
                <div className="container" style={{ overflow: 'auto' }}>
                    <div className="add_btn mt-2">
                        <Link to='/addForm'>
                            <button className='btn btn-primary'><i class="fa-sharp fa-solid fa-plus mr-2"></i>ADD NEW</button>
                        </Link>
                    </div>

                    <table class="table mt-5">
                        <thead>
                            <tr>
                                <th scope="col">Name</th>
                                <th scope="col">Email</th>
                                <th scope="col">Password</th>
                                <th scope="col">Mobile No</th>
                                <th scope="col">Address</th>
                                <th scope="col">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>

                                <td>Akash</td>
                                <td>akash12@gmail.com</td>
                                <td>1234</td>
                                <td>9876543221</td>
                                <td>Ahmedabad</td>
                                <td>
                                    <Link to="viewdata"><button type="button" class="btn btn-success" style={{ marginRight: '10px' }}>view</button></Link>
                                    <Link to="/editform"><button type="button" class="btn btn-primary" style={{ marginRight: '10px' }}>Edit</button></Link>
                                    <button type="button" class="btn btn-danger" style={{ marginRight: '10px' }}>Delete</button>
                                </td>
                            </tr>
                            {/* <tr>

                                <td>Ronak</td>
                                <td>ronakpatel1@gmail.com</td>
                                <td>9876</td>
                                <td>7878976565</td>
                                <td>Baroda</td>
                                <td>
                                    <button type="button" class="btn btn-success" style={{ marginRight: '10px' }}>view</button>
                                    <button type="button" class="btn btn-primary" style={{ marginRight: '10px' }}>Edit</button>
                                    <button type="button" class="btn btn-danger" style={{ marginRight: '10px' }}>Delete</button>
                                </td>
                            </tr> */}
                        </tbody>
                    </table>

                </div>
            </div>
        </>
    )
}

export default Home
